!
! in explicit double precision(!!!), note down-conversion might eventually occur
! RADIAN  : degrees per radian
! PI      : what can i say ...
! ROOT26  : 2 ** (1./6.)
!
module math
!
  real(KIND=8) RADIAN,PI,ROOT26,LOGMULTI
  integer FALSE2INT,TRUE2INT,LOGSHIFT
  parameter (RADIAN=57.29577951308232088)
  parameter (PI=3.141592653589793238)
  parameter (ROOT26=1.122462048309372981)
!
end module math
!
